package ch.protonmail.android.testdata.composer

import ch.protonmail.android.mailcomposer.domain.model.DraftRecipient
import ch.protonmail.android.mailcomposer.domain.model.DraftRecipientValidity
import ch.protonmail.android.mailpadlocks.domain.PrivacyLock

object DraftRecipientTestData {

    val Doe = build(
        address = "doe@pm.me",
        name = "Doe"
    )

    val John = build(
        address = "john@pm.me",
        name = "John"
    )

    val Alice = build(
        address = "alice@pm.me",
        name = "Alice"
    )

    val Bob = build(
        address = "bob@pm.me",
        name = "Bob"
    )

    val NamelessRecipient = build(
        address = "i-have-no-name@proton.me",
        name = ""
    )

    val MailToRecipient = build(
        address = "info@proton.me",
        name = ""
    )

    val ExternalRecipient = build(
        address = "external@gmail.com",
        name = ""
    )

    fun build(
        address: String = "email@pm.me",
        name: String,
        privacyLock: PrivacyLock = PrivacyLock.None
    ) = DraftRecipient.SingleRecipient(
        name,
        address,
        DraftRecipientValidity.Validating,
        privacyLock
    )
}
